package sm2;

import org.bouncycastle.cert.X509CertificateHolder;
import org.bouncycastle.cert.jcajce.JcaX509CertificateConverter;
import org.bouncycastle.cms.*;
import org.bouncycastle.cms.jcajce.JcaSimpleSignerInfoVerifierBuilder;
import org.bouncycastle.jce.provider.BouncyCastleProvider;
import org.bouncycastle.operator.OperatorCreationException;
import org.bouncycastle.util.Store;
import org.bouncycastle.util.encoders.Base64;

import java.io.ByteArrayInputStream;
import java.io.InputStream;
import java.security.Security;
import java.security.cert.CertificateException;
import java.security.cert.X509Certificate;
import java.util.Collection;
import java.util.Iterator;

public class VerifyPDF {
    public static String base64data = "";
    public static String base64pkcs7 = "";

    public static String base64data2 = "";
    public static String base64pkcs72 = "";

    public static String contentInfo = "";
    public static String text ="VGhpcyBpcyBhIG1lc3NhZ2U=";

    public static boolean verifyPkcs7() throws CertificateException, OperatorCreationException, CMSException {
        Security.addProvider(new BouncyCastleProvider());
        boolean ret = false;
        //CMSSignedData cms = new CMSSignedData(Base64.decode(base64pkcs7));

        //Create a CMSProcessable object, specify any encoding, I have used mine
        CMSProcessable signedContent = new CMSProcessableByteArray(Base64.decode(text));
        //Create a InputStream object
        InputStream is = new ByteArrayInputStream(Base64.decode(contentInfo));
        //Pass them both to CMSSignedData constructor
        CMSSignedData signedData = new CMSSignedData(signedContent, is);


        Store store = signedData.getCertificates();
        SignerInformationStore signers = signedData.getSignerInfos();

        Collection c = signers.getSigners();
        Iterator it = c.iterator();

        while (it.hasNext()) {
            SignerInformation signer = (SignerInformation)it.next();

            Collection certCollection = store.getMatches(signer.getSID());
            Iterator certIt = certCollection.iterator();

            X509CertificateHolder certHolder = (X509CertificateHolder)certIt.next();
            X509Certificate cert = new JcaX509CertificateConverter().setProvider("BC").getCertificate(certHolder);

            if (signer.verify(new JcaSimpleSignerInfoVerifierBuilder().setProvider("BC").build(cert))) {
                ret = true;
            }
        }

        return ret;
    }

    public static void main(String[] args) {
        try {
            System.out.println(verifyPkcs7());
        } catch (CertificateException e) {
            e.printStackTrace();
        } catch (OperatorCreationException e) {
            e.printStackTrace();
        } catch (CMSException e) {
            e.printStackTrace();
        }
    }
}
